import { Component } from '@angular/core';
import { OnModuleInit } from '@nestjs/common';
import { map } from 'rxjs';
import { GetGamesGQL, GetNodesGQL } from './state/gql';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnModuleInit {
  title = 'server-manager';

  public readonly nodes$ = this.getNodes
    .watch({})
    .valueChanges
    .pipe(map((result) => result.data.nodes));


  public readonly games$ = this.getNodes
  .watch({})
  .valueChanges
  .pipe(map((result) => result.data.nodes));

  constructor(
    protected readonly getNodes: GetNodesGQL,
    protected readonly getGames: GetGamesGQL,
    protected readonly watch
  ) {
    console.log("graou")
  }

  onModuleInit() {
    console.log("issous", this.nodes$)
  }
}
